INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15456, 'nvi', 'Salmos', 'sl', 95, 1, 'Venham! Cantemos ao Senhor com alegria! Aclamemos a Rocha da nossa salvação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15457, 'nvi', 'Salmos', 'sl', 95, 2, 'Vamos à presença dele com ações de graças; vamos aclamá-lo com cânticos de louvor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15458, 'nvi', 'Salmos', 'sl', 95, 3, 'Pois o Senhor é o grande Deus, o grande Rei acima de todos os deuses.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15459, 'nvi', 'Salmos', 'sl', 95, 4, 'Nas suas mãos estão as profundezas da terra, os cumes dos montes lhe pertencem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15460, 'nvi', 'Salmos', 'sl', 95, 5, 'Dele também é o mar, pois ele o fez; as suas mãos formaram a terra seca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15461, 'nvi', 'Salmos', 'sl', 95, 6, 'Venham! Adoremos prostrados e ajoelhemos diante do Senhor, o nosso Criador;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15462, 'nvi', 'Salmos', 'sl', 95, 7, 'pois ele é o nosso Deus, e nós somos o povo do seu pastoreio, o rebanho que ele conduz. Hoje, se vocês ouvirem a sua voz,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15463, 'nvi', 'Salmos', 'sl', 95, 8, 'não endureçam o coração, como em Meribá, como aquele dia em Massá, no deserto,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15464, 'nvi', 'Salmos', 'sl', 95, 9, 'onde os seus antepassados me tentaram, pondo-me à prova, apesar de terem visto o que eu fiz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15465, 'nvi', 'Salmos', 'sl', 95, 10, 'Durante quarenta anos fiquei irado contra aquela geração e disse: "Eles são um povo de coração ingrato; não reconheceram os meus caminhos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15466, 'nvi', 'Salmos', 'sl', 95, 11, 'Por isso jurei na minha ira: "Jamais entrarão no meu descanso".');
