INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15467, 'nvi', 'Salmos', 'sl', 96, 1, 'Cantem ao Senhor um novo cântico; cantem ao Senhor, todos os habitantes da terra!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15468, 'nvi', 'Salmos', 'sl', 96, 2, 'Cantem ao Senhor, bendigam o seu nome; cada dia proclamem a sua salvação!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15469, 'nvi', 'Salmos', 'sl', 96, 3, 'Anunciem a sua glória entre as nações, seus feitos maravilhosos entre todos os povos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15470, 'nvi', 'Salmos', 'sl', 96, 4, 'Porque o Senhor é grande e digno de todo louvor, mais temível do que todos os deuses!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15471, 'nvi', 'Salmos', 'sl', 96, 5, 'Todos os deuses das nações não passam de ídolos, mas o Senhor fez os céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15472, 'nvi', 'Salmos', 'sl', 96, 6, 'Majestade e esplendor estão diante dele, poder e dignidade, no seu santuário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15473, 'nvi', 'Salmos', 'sl', 96, 7, 'Dêem ao Senhor, ó famílias das nações, dêem ao Senhor glória e força.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15474, 'nvi', 'Salmos', 'sl', 96, 8, 'Dêem ao Senhor a glória devida ao seu nome, e entrem nos seus átrios trazendo ofertas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15475, 'nvi', 'Salmos', 'sl', 96, 9, 'Adorem ao Senhor no esplendor da sua santidade; tremam diante dele todos os habitantes da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15476, 'nvi', 'Salmos', 'sl', 96, 10, 'Digam entre as nações: "O Senhor reina! " Por isso firme está o mundo, e não se abalará, e ele julgará os povos com justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15477, 'nvi', 'Salmos', 'sl', 96, 11, 'Regozijem-se os céus e exulte a terra! Ressoe o mar e tudo o que nele existe!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15478, 'nvi', 'Salmos', 'sl', 96, 12, 'Regozijem-se os campos e tudo o que neles há! Cantem de alegria todas as árvores da floresta,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15479, 'nvi', 'Salmos', 'sl', 96, 13, 'cantem diante do Senhor, porque ele vem, vem julgar a terra; julgará o mundo com justiça e os povos, com a sua fidelidade!');
