INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15480, 'nvi', 'Salmos', 'sl', 97, 1, 'O Senhor reina! Exulte a terra e alegrem-se as regiões costeiras distantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15481, 'nvi', 'Salmos', 'sl', 97, 2, 'Nuvens escuras e espessas o cercam; retidão e justiça são a base do seu trono.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15482, 'nvi', 'Salmos', 'sl', 97, 3, 'Fogo vai adiante dele e devora os adversários ao redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15483, 'nvi', 'Salmos', 'sl', 97, 4, 'Seus relâmpagos iluminam o mundo; a terra os vê e estremece.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15484, 'nvi', 'Salmos', 'sl', 97, 5, 'Os montes se derretem como cera diante do Senhor, diante do Soberano de toda a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15485, 'nvi', 'Salmos', 'sl', 97, 6, 'Os céus proclamam a sua justiça, e todos os povos contemplam a sua glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15486, 'nvi', 'Salmos', 'sl', 97, 7, 'Ficam decepcionados todos os que adoram imagens e se vangloriam de ídolos. Prostram-se diante dele todos os deuses!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15487, 'nvi', 'Salmos', 'sl', 97, 8, 'Sião ouve e se alegra, e as cidades de Judá exultam, por causa das tuas sentenças, Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15488, 'nvi', 'Salmos', 'sl', 97, 9, 'Pois tu, Senhor, és o Altíssimo sobre toda a terra! És exaltado muito acima de todos os deuses!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15489, 'nvi', 'Salmos', 'sl', 97, 10, 'Odeiem o mal, vocês que amam o Senhor, pois ele protege a vida dos seus fiéis e os livra das mãos dos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15490, 'nvi', 'Salmos', 'sl', 97, 11, 'A luz nasce sobre o justo e a alegria sobre os retos de coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15491, 'nvi', 'Salmos', 'sl', 97, 12, 'Alegrem-se no Senhor, justos, louvem o seu santo nome.');
