INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15492, 'nvi', 'Salmos', 'sl', 98, 1, 'Cantem ao Senhor um novo cântico, pois ele fez coisas maravilhosas; a sua mão direita e o seu braço santo lhe deram a vitória!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15493, 'nvi', 'Salmos', 'sl', 98, 2, 'O Senhor anunciou a sua vitória e revelou a sua justiça às nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15494, 'nvi', 'Salmos', 'sl', 98, 3, 'Ele se lembrou do seu amor leal e da sua fidelidade para com a casa de Israel; todos os confins da terra viram a vitória do nosso Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15495, 'nvi', 'Salmos', 'sl', 98, 4, 'Aclamem o Senhor todos os habitantes da terra! Louvem-no com cânticos de alegria e ao som de música!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15496, 'nvi', 'Salmos', 'sl', 98, 5, 'Ofereçam música ao Senhor com a harpa, com a harpa e ao som de canções,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15497, 'nvi', 'Salmos', 'sl', 98, 6, 'com cornetas e ao som da trombeta; exultem diante do Senhor, o Rei!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15498, 'nvi', 'Salmos', 'sl', 98, 7, 'Ressoe o mar e tudo o que nele existe, o mundo e os seus habitantes!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15499, 'nvi', 'Salmos', 'sl', 98, 8, 'Batam palmas os rios, e juntos, cantem de alegria os montes;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15500, 'nvi', 'Salmos', 'sl', 98, 9, 'cantem diante do Senhor, porque ele vem, vem julgar a terra; julgará o mundo com justiça e os povos, com retidão.');
