INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15501, 'nvi', 'Salmos', 'sl', 99, 1, 'O Senhor reina! As nações tremem! O seu trono está sobre os querubins! Abala-se a terra!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15502, 'nvi', 'Salmos', 'sl', 99, 2, 'Grande é o Senhor em Sião; ele é exaltado acima de todas as nações!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15503, 'nvi', 'Salmos', 'sl', 99, 3, 'Seja louvado o teu grande e temível nome, que é santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15504, 'nvi', 'Salmos', 'sl', 99, 4, 'Rei poderoso, amigo da justiça! Estabeleceste a eqüidade e fizeste em Jacó o que é direito e justo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15505, 'nvi', 'Salmos', 'sl', 99, 5, 'Exaltem o Senhor, o nosso Deus, prostrem-se diante do estrado dos seus pés. Ele é santo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15506, 'nvi', 'Salmos', 'sl', 99, 6, 'Moisés e Arão estavam entre os seus sacerdotes, Samuel, entre os que invocavam o seu nome; eles clamavam pelo Senhor, e ele lhes respondia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15507, 'nvi', 'Salmos', 'sl', 99, 7, 'Falava-lhes da coluna de nuvem, e eles obedeciam aos seus mandamentos e aos decretos que ele lhes dava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15508, 'nvi', 'Salmos', 'sl', 99, 8, 'Tu lhes respondeste, Senhor, nosso Deus; para eles, tu eras um Deus perdoador, embora os tenhas castigado por suas rebeliões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15509, 'nvi', 'Salmos', 'sl', 99, 9, 'Exaltem o Senhor nosso Deus; prostrem-se, voltados para o seu santo monte, porque o Senhor, o nosso Deus, é santo.');
