INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30268, 'nvi', 'Tiago', 'tg', 1, 1, 'Tiago, servo de Deus e do Senhor Jesus Cristo, às doze tribos dispersas entre as nações: Saudações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30269, 'nvi', 'Tiago', 'tg', 1, 2, 'Meus irmãos, considerem motivo de grande alegria o fato de passarem por diversas provações,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30270, 'nvi', 'Tiago', 'tg', 1, 3, 'pois vocês sabem que a prova da sua fé produz perseverança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30271, 'nvi', 'Tiago', 'tg', 1, 4, 'E a perseverança deve ter ação completa, a fim de que vocês sejam maduros e íntegros, sem lhes faltar coisa alguma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30272, 'nvi', 'Tiago', 'tg', 1, 5, 'Se algum de vocês tem falta de sabedoria, peça-a a Deus, que a todos dá livremente, de boa vontade; e lhe será concedida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30273, 'nvi', 'Tiago', 'tg', 1, 6, 'Peça-a, porém, com fé, sem duvidar, pois aquele que duvida é semelhante à onda do mar, levada e agitada pelo vento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30274, 'nvi', 'Tiago', 'tg', 1, 7, 'Não pense tal homem que receberá coisa alguma do Senhor;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30275, 'nvi', 'Tiago', 'tg', 1, 8, 'é alguém que tem mente dividida e é instável em tudo o que faz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30276, 'nvi', 'Tiago', 'tg', 1, 9, 'O irmão de condição humilde deve orgulhar-se quando estiver em elevada posição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30277, 'nvi', 'Tiago', 'tg', 1, 10, 'E o rico deve orgulhar-se se passar a viver em condição humilde, porque passará como a flor do campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30278, 'nvi', 'Tiago', 'tg', 1, 11, 'Pois o sol se levanta, traz o calor e seca a planta; cai então a sua flor, e é destruída a beleza da sua aparência. Da mesma forma o rico murchará em meio aos seus afazeres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30279, 'nvi', 'Tiago', 'tg', 1, 12, 'Feliz é o homem que persevera na provação, porque depois de aprovado receberá a coroa da vida que Deus prometeu aos que o amam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30280, 'nvi', 'Tiago', 'tg', 1, 13, 'Quando alguém for tentado, jamais deverá dizer: "Estou sendo tentado por Deus". Pois Deus não pode ser tentado pelo mal, e a ninguém tenta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30281, 'nvi', 'Tiago', 'tg', 1, 14, 'Cada um, porém, é tentado pela própria cobiça, sendo por esta arrastado e seduzido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30282, 'nvi', 'Tiago', 'tg', 1, 15, 'Então a cobiça, tendo engravidado, dá à luz o pecado; e o pecado, após ter-se consumado, gera a morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30283, 'nvi', 'Tiago', 'tg', 1, 16, 'Meus amados irmãos, não se deixem enganar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30284, 'nvi', 'Tiago', 'tg', 1, 17, 'Toda boa dádiva e todo dom perfeito vêm do alto, descendo do Pai das luzes, que não muda como sombras inconstantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30285, 'nvi', 'Tiago', 'tg', 1, 18, 'Por sua decisão ele nos gerou pela palavra da verdade, para que sejamos como que os primeiros frutos de tudo o que ele criou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30286, 'nvi', 'Tiago', 'tg', 1, 19, 'Meus amados irmãos, tenham isto em mente: Sejam todos prontos para ouvir, tardios para falar e tardios para irar-se,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30287, 'nvi', 'Tiago', 'tg', 1, 20, 'pois a ira do homem não produz a justiça de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30288, 'nvi', 'Tiago', 'tg', 1, 21, 'Portanto, livrem-se de toda impureza moral e da maldade que prevalece, e aceitem humildemente a palavra implantada em vocês, a qual é poderosa para salvá-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30289, 'nvi', 'Tiago', 'tg', 1, 22, 'Sejam praticantes da palavra, e não apenas ouvintes, enganando-se a si mesmos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30290, 'nvi', 'Tiago', 'tg', 1, 23, 'Aquele que ouve a palavra, mas não a põe em prática, é semelhante a um homem que olha a sua face num espelho');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30291, 'nvi', 'Tiago', 'tg', 1, 24, 'e, depois de olhar para si mesmo, sai e logo esquece a sua aparência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30292, 'nvi', 'Tiago', 'tg', 1, 25, 'Mas o homem que observa atentamente a lei perfeita que traz a liberdade, e persevera na prática dessa lei, não esquecendo o que ouviu mas praticando-o, será feliz naquilo que fizer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30293, 'nvi', 'Tiago', 'tg', 1, 26, 'Se alguém se considera religioso, mas não refreia a sua língua, engana-se a si mesmo. Sua religião não tem valor algum!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30294, 'nvi', 'Tiago', 'tg', 1, 27, 'A religião que Deus, o nosso Pai aceita como pura e imaculada é esta: cuidar dos órfãos e das viúvas em suas dificuldades e não se deixar corromper pelo mundo.');
