INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30295, 'nvi', 'Tiago', 'tg', 2, 1, 'Meus irmãos, como crentes em nosso glorioso Senhor Jesus Cristo, não façam diferença entre as pessoas, tratando-as com favoritismo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30296, 'nvi', 'Tiago', 'tg', 2, 2, 'Suponham que na reunião de vocês entre um homem com anel de ouro e roupas finas, e também entre um homem pobre com roupas velhas e sujas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30297, 'nvi', 'Tiago', 'tg', 2, 3, 'Se vocês derem atenção especial ao homem que está vestido com roupas finas e disserem: "Aqui está um lugar apropriado para o senhor", mas disserem ao pobre: "Você, fique de pé ali", ou: "Sente-se no chão, junto ao estrado onde ponho os meus pés",');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30298, 'nvi', 'Tiago', 'tg', 2, 4, 'não estarão fazendo discriminação, fazendo julgamentos com critérios errados?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30299, 'nvi', 'Tiago', 'tg', 2, 5, 'Ouçam, meus amados irmãos: não escolheu Deus os que são pobres aos olhos do mundo para serem ricos em fé e herdarem o Reino que ele prometeu aos que o amam?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30300, 'nvi', 'Tiago', 'tg', 2, 6, 'Mas vocês têm desprezado o pobre. Não são os ricos que oprimem vocês? Não são eles os que os arrastam para os tribunais?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30301, 'nvi', 'Tiago', 'tg', 2, 7, 'Não são eles que difamam o bom nome que sobre vocês foi invocado?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30302, 'nvi', 'Tiago', 'tg', 2, 8, 'Se vocês de fato obedecerem à lei real encontrada na Escritura que diz: "Ame o seu próximo como a si mesmo", estarão agindo corretamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30303, 'nvi', 'Tiago', 'tg', 2, 9, 'Mas se tratarem os outros com favoritismo, estarão cometendo pecado e serão condenados pela Lei como transgressores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30304, 'nvi', 'Tiago', 'tg', 2, 10, 'Pois quem obedece a toda a Lei, mas tropeça em apenas um ponto, torna-se culpado de quebrá-la inteiramente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30305, 'nvi', 'Tiago', 'tg', 2, 11, 'Pois aquele que disse: "Não adulterarás", também disse: "Não matarás". Se você não comete adultério, mas comete assassinato, torna-se transgressor da Lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30306, 'nvi', 'Tiago', 'tg', 2, 12, 'Falem e ajam como quem vai ser julgado pela lei da liberdade;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30307, 'nvi', 'Tiago', 'tg', 2, 13, 'porque será exercido juízo sem misericórdia sobre quem não foi misericordioso. A misericórdia triunfa sobre o juízo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30308, 'nvi', 'Tiago', 'tg', 2, 14, 'De que adianta, meus irmãos, alguém dizer que tem fé, se não tem obras? Acaso a fé pode salvá-lo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30309, 'nvi', 'Tiago', 'tg', 2, 15, 'Se um irmão ou irmã estiver necessitando de roupas e do alimento de cada dia');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30310, 'nvi', 'Tiago', 'tg', 2, 16, 'e um de vocês lhe disser: "Vá em paz, aqueça-se e alimente-se até satisfazer-se", sem porém lhe dar nada, de que adianta isso?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30311, 'nvi', 'Tiago', 'tg', 2, 17, 'Assim também a fé, por si só, se não for acompanhada de obras, está morta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30312, 'nvi', 'Tiago', 'tg', 2, 18, 'Mas alguém dirá: "Você tem fé; eu tenho obras". Mostre-me a sua fé sem obras, e eu lhe mostrarei a minha fé pelas obras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30313, 'nvi', 'Tiago', 'tg', 2, 19, 'Você crê que existe um só Deus? Muito bem! Até mesmo os demônios crêem — e tremem!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30314, 'nvi', 'Tiago', 'tg', 2, 20, 'Insensato! Quer certificar-se de que a fé sem obras é inútil?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30315, 'nvi', 'Tiago', 'tg', 2, 21, 'Não foi Abraão, nosso antepassado, justificado por obras, quando ofereceu seu filho Isaque sobre o altar?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30316, 'nvi', 'Tiago', 'tg', 2, 22, 'Você pode ver que tanto a fé como as suas obras estavam atuando juntas, e a fé foi aperfeiçoada pelas obras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30317, 'nvi', 'Tiago', 'tg', 2, 23, 'Cumpriu-se assim a Escritura que diz: "Abraão creu em Deus, e isso lhe foi creditado como justiça", e ele foi chamado amigo de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30318, 'nvi', 'Tiago', 'tg', 2, 24, 'Vejam que uma pessoa é justificada por obras, e não apenas pela fé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30319, 'nvi', 'Tiago', 'tg', 2, 25, 'Caso semelhante é o de Raabe, a prostituta: não foi ela justificada pelas obras, quando acolheu os espias e os fez sair por outro caminho?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30320, 'nvi', 'Tiago', 'tg', 2, 26, 'Assim como o corpo sem espírito está morto, também a fé sem obras está morta.');
