INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30356, 'nvi', 'Tiago', 'tg', 5, 1, 'Ouçam agora vocês, ricos! Chorem e lamentem-se, tendo em vista a miséria que lhes sobrevirá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30357, 'nvi', 'Tiago', 'tg', 5, 2, 'A riqueza de vocês apodreceu, e as traças corroeram as suas roupas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30358, 'nvi', 'Tiago', 'tg', 5, 3, 'O ouro e a prata de vocês enferrujaram, e a ferrugem deles testemunhará contra vocês e como fogo lhes devorará a carne. Vocês acumularam bens nestes últimos dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30359, 'nvi', 'Tiago', 'tg', 5, 4, 'Vejam, o salário dos trabalhadores que ceifaram os seus campos, e que por vocês foi retido com fraude, está clamando contra vocês. O lamento dos ceifeiros chegou aos ouvidos do Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30360, 'nvi', 'Tiago', 'tg', 5, 5, 'Vocês viveram luxuosamente na terra, desfrutando prazeres, e fartaram-se de comida em dia de abate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30361, 'nvi', 'Tiago', 'tg', 5, 6, 'Vocês têm condenado e matado o justo, sem que ele ofereça resistência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30362, 'nvi', 'Tiago', 'tg', 5, 7, 'Portanto, irmãos, sejam pacientes até a vinda do Senhor. Vejam como o agricultor aguarda que a terra produza a preciosa colheita e como espera com paciência até virem as chuvas do outono e da primavera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30363, 'nvi', 'Tiago', 'tg', 5, 8, 'Sejam também pacientes e fortaleçam o coração, pois a vinda do Senhor está próxima.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30364, 'nvi', 'Tiago', 'tg', 5, 9, 'Irmãos, não se queixem uns dos outros, para que não sejam julgados. O Juiz já está às portas!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30365, 'nvi', 'Tiago', 'tg', 5, 10, 'Irmãos, tenham os profetas que falaram em nome do Senhor como exemplo de paciência diante do sofrimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30366, 'nvi', 'Tiago', 'tg', 5, 11, 'Como vocês sabem, nós consideramos felizes aqueles que mostraram perseverança. Vocês ouviram falar sobre a paciência de Jó e viram o fim que o Senhor lhe proporcionou. O Senhor é cheio de compaixão e misericórdia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30367, 'nvi', 'Tiago', 'tg', 5, 12, 'Sobretudo, meus irmãos, não jurem nem pelo céu, nem pela terra, nem por qualquer outra coisa. Seja o sim de vocês, sim, e o não, não, para que não caiam em condenação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30368, 'nvi', 'Tiago', 'tg', 5, 13, 'Entre vocês há alguém que está sofrendo? Que ele ore. Há alguém que se sente feliz? Que ele cante louvores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30369, 'nvi', 'Tiago', 'tg', 5, 14, 'Entre vocês há alguém que está doente? Que ele mande chamar os presbíteros da igreja, para que estes orem sobre ele e o unjam com óleo, em nome do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30370, 'nvi', 'Tiago', 'tg', 5, 15, 'E a oração feita com fé curará o doente; o Senhor o levantará. E se houver cometido pecados, ele será perdoado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30371, 'nvi', 'Tiago', 'tg', 5, 16, 'Portanto, confessem os seus pecados uns aos outros e orem uns pelos outros para serem curados. A oração de um justo é poderosa e eficaz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30372, 'nvi', 'Tiago', 'tg', 5, 17, 'Elias era humano como nós. Ele orou fervorosamente para que não chovesse, e não choveu sobre a terra durante três anos e meio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30373, 'nvi', 'Tiago', 'tg', 5, 18, 'Orou outra vez, e o céu enviou chuva, e a terra produziu os seus frutos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30374, 'nvi', 'Tiago', 'tg', 5, 19, 'Meus irmãos, se algum de vocês se desviar da verdade e alguém o trouxer de volta,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30375, 'nvi', 'Tiago', 'tg', 5, 20, 'lembrem-se disso: Quem converte um pecador do erro do seu caminho, salvará a vida dessa pessoa e fará que muitíssimos pecados sejam perdoados.');
