INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22880, 'nvi', 'Zacarias', 'zc', 1, 1, 'No oitavo mês do segundo ano do reinado de Dario, a palavra do Senhor veio ao profeta Zacarias, filho de Berequias e neto de Ido:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22881, 'nvi', 'Zacarias', 'zc', 1, 2, '"O Senhor muito se irou contra os seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22882, 'nvi', 'Zacarias', 'zc', 1, 3, 'Por isso diga ao povo: Assim diz o Senhor dos Exércitos: ‘Voltem para mim, e eu me voltarei para vocês’, diz o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22883, 'nvi', 'Zacarias', 'zc', 1, 4, 'Não sejam como os seus antepassados aos quais os antigos profetas proclamaram: Assim diz o Senhor dos Exércitos: ‘Deixem os seus caminhos e as suas más obras. Mas eles não me ouviram nem me deram atenção’, declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22884, 'nvi', 'Zacarias', 'zc', 1, 5, 'Onde estão agora os seus antepassados? E os profetas, acaso vivem eles para sempre?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22885, 'nvi', 'Zacarias', 'zc', 1, 6, 'Mas as minhas palavras e os meus decretos, que ordenei aos meus servos, os profetas, alcançaram os seus antepassados e os levaram a converter-se e a dizer: ‘O Senhor dos Exércitos fez conosco o que os nossos caminhos e práticas mereciam, conforme prometeu’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22886, 'nvi', 'Zacarias', 'zc', 1, 7, 'No dia vigésimo quarto do décimo primeiro mês, o mês de sebate, no segundo ano do reinado de Dario, a palavra do Senhor veio ao profeta Zacarias, filho de Berequias e neto de Ido:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22887, 'nvi', 'Zacarias', 'zc', 1, 8, 'Durante a noite tive uma visão; apareceu na minha frente um homem montado num cavalo vermelho. Ele estava parado entre as murtas num desfiladeiro. Atrás dele havia cavalos vermelhos, marrons e brancos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22888, 'nvi', 'Zacarias', 'zc', 1, 9, 'Então perguntei: Quem são estes, meu senhor? O anjo que estava falando comigo respondeu: "Eu lhe mostrarei quem são".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22889, 'nvi', 'Zacarias', 'zc', 1, 10, 'Então o homem que estava entre as murtas explicou: "São aqueles que o Senhor enviou por toda a terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22890, 'nvi', 'Zacarias', 'zc', 1, 11, 'E eles relataram ao anjo do Senhor que estava entre as murtas: "Percorremos toda a terra e a encontramos em paz e tranqüila".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22891, 'nvi', 'Zacarias', 'zc', 1, 12, 'Então o anjo do Senhor respondeu: "Senhor dos Exércitos, até quando deixarás de ter misericórdia de Jerusalém e das cidades de Judá, com as quais estás indignado há setenta anos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22892, 'nvi', 'Zacarias', 'zc', 1, 13, 'Então o Senhor respondeu palavras boas e confortadoras ao anjo que falava comigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22893, 'nvi', 'Zacarias', 'zc', 1, 14, 'E o anjo me disse: "Proclame: Assim diz o Senhor dos Exércitos: ‘Eu tenho sido muito zeloso com Jerusalém e Sião,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22894, 'nvi', 'Zacarias', 'zc', 1, 15, 'mas estou muito irado contra as nações que se sentem seguras. Porque eu estava apenas um pouco irado com meu povo, mas elas aumentaram a dor que ele sofria! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22895, 'nvi', 'Zacarias', 'zc', 1, 16, '"Por isso, assim diz o Senhor: ‘Estou voltando-me para Jerusalém com misericórdia, e ali o meu templo será reconstruído. A corda de medir será esticada sobre Jerusalém’, declara o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22896, 'nvi', 'Zacarias', 'zc', 1, 17, '"Diga mais: Assim diz o Senhor dos Exércitos: ‘As minhas cidades transbordarão de prosperidade novamente, e o Senhor consolará novamente a Sião e escolherá Jerusalém’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22897, 'nvi', 'Zacarias', 'zc', 1, 18, 'Depois eu olhei para o alto, e vi quatro chifres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22898, 'nvi', 'Zacarias', 'zc', 1, 19, 'Então perguntei ao anjo que falava comigo: "O que são estes? " Ele me respondeu: "Estes são os chifres que dispersaram Judá, Israel e Jerusalém".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22899, 'nvi', 'Zacarias', 'zc', 1, 20, 'Depois o Senhor mostrou-me quatro artesãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22900, 'nvi', 'Zacarias', 'zc', 1, 21, 'Eu perguntei: "O que eles vêm fazer? " Ele respondeu: "Estes são os chifres que dispersaram Judá a ponto de ninguém sequer conseguir levantar a cabeça, mas os artesãos vieram aterrorizar e quebrar esses chifres das nações que se levantaram contra o povo de Judá para dispersá-lo".');
