INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23070, 'nvi', 'Zacarias', 'zc', 14, 1, 'Vejam, o dia do Senhor vem, quando no meio de vocês os seus bens serão divididos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23071, 'nvi', 'Zacarias', 'zc', 14, 2, 'Reunirei todos os povos para lutarem contra Jerusalém; a cidade será conquistada, as casas saqueadas e as mulheres violentadas. Metade da população será levada para o exílio, mas o restante do povo não será tirado da cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23072, 'nvi', 'Zacarias', 'zc', 14, 3, 'Depois o Senhor sairá à guerra contra aquelas nações, como ele faz em dia de batalha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23073, 'nvi', 'Zacarias', 'zc', 14, 4, 'Naquele dia os seus pés estarão sobre o monte das Oliveiras, a leste de Jerusalém, e o monte se dividirá ao meio, de leste a oeste, por um grande vale, metade do monte será removido para o norte, a outra metade para o sul.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23074, 'nvi', 'Zacarias', 'zc', 14, 5, 'Vocês fugirão pelo meu vale entre os montes, pois ele se estenderá até Azel. Fugirão como fugiram do terremoto nos dias de Uzias, rei de Judá. Então o Senhor, o meu Deus, virá com todos os seus santos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23075, 'nvi', 'Zacarias', 'zc', 14, 6, 'Naquele dia não haverá calor nem frio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23076, 'nvi', 'Zacarias', 'zc', 14, 7, 'Será um dia único, no qual não haverá separação entre dia e noite, porque quando chegar a noite ainda estará claro. Um dia que o Senhor conhece.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23077, 'nvi', 'Zacarias', 'zc', 14, 8, 'Naquele dia águas correntes fluirão de Jerusalém, metade delas para o mar do leste e metade para o mar do oeste. Isto acontecerá tanto no verão quanto no inverno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23078, 'nvi', 'Zacarias', 'zc', 14, 9, 'O Senhor será rei de toda a terra. Naquele dia haverá um só Senhor e o seu nome será o único nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23079, 'nvi', 'Zacarias', 'zc', 14, 10, 'A terra toda, de Geba até Rimom, ao sul de Jerusalém, será semelhante à Arabá. Mas Jerusalém será restabelecida e permanecerá em seu lugar, desde a porta de Benjamim até o lugar da primeira porta, até a porta da Esquina, e desde a torre de Hananeel até aos lagares do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23080, 'nvi', 'Zacarias', 'zc', 14, 11, 'Será habitada; nunca mais será destruída. Jerusalém estará segura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23081, 'nvi', 'Zacarias', 'zc', 14, 12, 'Esta é a praga com a qual o Senhor castigará todas as nações que lutarem contra Jerusalém: Sua carne apodrecerá enquanto estiverem ainda em pé, seus olhos apodrecerão em suas órbitas e sua língua apodrecerá dentro de suas bocas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23082, 'nvi', 'Zacarias', 'zc', 14, 13, 'Naquele dia grande confusão dominará essas nações, causada pelo Senhor. Cada um atacará o que estiver ao seu lado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23083, 'nvi', 'Zacarias', 'zc', 14, 14, 'Também Judá lutará em Jerusalém. A riqueza de todas as nações vizinhas será recolhida, grandes quantidades de ouro, prata e roupas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23084, 'nvi', 'Zacarias', 'zc', 14, 15, 'A mesma praga cairá sobre cavalos e mulas, camelos e burros, sobre todos os animais daquelas nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23085, 'nvi', 'Zacarias', 'zc', 14, 16, 'Então, os sobreviventes de todas as nações que atacaram Jerusalém subirão ano após ano para adorar o rei, o Senhor dos Exércitos, para celebrar a festa das Cabanas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23086, 'nvi', 'Zacarias', 'zc', 14, 17, 'Se algum dentre os povos da terra não subir a Jerusalém para adorar o Rei, o Senhor dos Exércitos, não virá para ele a chuva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23087, 'nvi', 'Zacarias', 'zc', 14, 18, 'Se os egípcios não subirem para participar, o Senhor mandará sobre eles a praga com a qual afligirá as nações que se recusarem a ir celebrar a festa das Cabanas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23088, 'nvi', 'Zacarias', 'zc', 14, 19, 'Sim, essa será a punição do Egito e de todas as nações que não forem celebrar a festa das Cabanas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23089, 'nvi', 'Zacarias', 'zc', 14, 20, 'Naquele dia estará inscrito nas sinetas penduradas nos cavalos: "Separado para o Senhor". Os caldeirões do templo do Senhor serão tão sagrados quanto as bacias diante do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23090, 'nvi', 'Zacarias', 'zc', 14, 21, 'Cada panela de Jerusalém e de Judá será separada para o Senhor dos Exércitos, e todos os que vierem sacrificar pegarão panelas e cozinharão nelas. E a partir daquele dia, nunca mais haverá comerciantes no templo do Senhor dos Exércitos.');
