INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22978, 'nvi', 'Zacarias', 'zc', 8, 1, 'Mais uma vez veio a mim a palavra do Senhor dos Exércitos:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22979, 'nvi', 'Zacarias', 'zc', 8, 2, 'Assim diz o Senhor dos Exércitos: "Tenho muito ciúme de Sião; estou me consumindo de ciúmes por ela".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22980, 'nvi', 'Zacarias', 'zc', 8, 3, 'Assim diz o Senhor: "Estou voltando para Sião e habitarei em Jerusalém. Então Jerusalém será chamada Cidade da Verdade, e o monte do Senhor dos Exércitos será chamado Monte Sagrado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22981, 'nvi', 'Zacarias', 'zc', 8, 4, 'Assim diz o Senhor dos Exércitos: "Homens e mulheres de idade avançada voltarão a sentar-se nas praças de Jerusalém, cada um com sua bengala, por causa da idade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22982, 'nvi', 'Zacarias', 'zc', 8, 5, 'As ruas da cidade ficarão cheias de meninos e meninas brincando".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22983, 'nvi', 'Zacarias', 'zc', 8, 6, 'Assim diz o Senhor dos Exércitos: "Mesmo que isso pareça impossível para o remanescente deste povo naquela época, será impossível para mim? ", declara o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22984, 'nvi', 'Zacarias', 'zc', 8, 7, 'Assim diz o Senhor dos Exércitos: "Salvarei meu povo dos países do leste e do oeste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22985, 'nvi', 'Zacarias', 'zc', 8, 8, 'Eu os trarei de volta para que habitem em Jerusalém; serão meu povo e eu serei o Deus deles, com fidelidade e justiça".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22986, 'nvi', 'Zacarias', 'zc', 8, 9, 'Assim diz o Senhor dos Exércitos: "Vocês que estão ouvindo hoje estas palavras já proferidas pelos profetas quando foram lançados os alicerces do templo do Senhor dos Exércitos, fortaleçam as mãos para que o templo seja construído.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22987, 'nvi', 'Zacarias', 'zc', 8, 10, 'Pois antes daquele tempo não havia salários para os homens nem para os animais. Ninguém podia tratar os seus negócios com segurança por causa de seus adversários, porque eu tinha posto cada um contra o seu próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22988, 'nvi', 'Zacarias', 'zc', 8, 11, 'Mas agora não vou mais tratar com o remanescente deste povo como fiz no passado", declara o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22989, 'nvi', 'Zacarias', 'zc', 8, 12, '"Haverá uma rica semeadura, a videira dará o seu fruto, a terra produzirá suas colheitas e o céu derramará o orvalho. E darei todas essas coisas como uma herança ao remanescente deste povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22990, 'nvi', 'Zacarias', 'zc', 8, 13, 'Assim como vocês foram uma maldição para as nações, ó Judá e Israel, também os salvarei e vocês serão uma bênção. Não tenham medo, antes sejam fortes. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22991, 'nvi', 'Zacarias', 'zc', 8, 14, 'Assim diz o Senhor dos Exércitos: "Assim como eu havia decidido castigar vocês sem compaixão quando os seus antepassados me enfureceram", diz o Senhor dos Exércitos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22992, 'nvi', 'Zacarias', 'zc', 8, 15, '"também agora decidi fazer de novo o bem a Jerusalém e a Judá. Não tenham medo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22993, 'nvi', 'Zacarias', 'zc', 8, 16, 'Eis o que devem fazer: Falem somente a verdade uns com os outros, e julguem retamente em seus tribunais;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22994, 'nvi', 'Zacarias', 'zc', 8, 17, 'não planejem no íntimo o mal contra o seu próximo, e não queiram jurar com falsidade. Porque eu odeio todas essas coisas", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22995, 'nvi', 'Zacarias', 'zc', 8, 18, 'Mais uma vez veio a mim a palavra do Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22996, 'nvi', 'Zacarias', 'zc', 8, 19, 'Assim diz o Senhor dos Exércitos: "Os jejuns do quarto, do quinto, do sétimo e do décimo meses serão ocasiões alegres e cheias de júbilo, festas felizes para o povo de Judá. Por isso amem a verdade e a paz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22997, 'nvi', 'Zacarias', 'zc', 8, 20, 'Assim diz o Senhor dos Exércitos: "Povos e habitantes de muitas cidades ainda virão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22998, 'nvi', 'Zacarias', 'zc', 8, 21, 'e os habitantes de uma cidade irão a outra e dirão: ‘Vamos logo suplicar o favor do Senhor e buscar o Senhor dos Exércitos. Eu mesmo já estou indo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22999, 'nvi', 'Zacarias', 'zc', 8, 22, 'E muitos povos e nações poderosas virão buscar o Senhor dos Exércitos em Jerusalém e suplicar o seu favor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (23000, 'nvi', 'Zacarias', 'zc', 8, 23, 'Assim diz o Senhor dos Exércitos: "Naqueles dias, dez homens de todas as línguas e nações agarrarão firmemente a barra das vestes de um judeu e dirão: ‘Nós vamos com você porque ouvimos dizer que Deus está com vocês’ ".');
